import styled from 'styled-components';

const Wrapper = styled.div`
  background-color: #d9dce2;
  border-radius: 6px;
  display: flex;
  flex-direction: column;
  margin: 0 16px;

  @media(min-width: 1024px) {
    flex-direction: row;
    height: 512px;
    justify-content: center;
    margin: 0 10%;
    padding: 24px;
  }
`;

const BioWrapper = styled.div`

  @media(min-width: 1024px) {
    font-size: 16px;
    padding: 24px;
  }
`;

const Bio = styled.p`

  @media(min-width: 1024px) {
    font-size: 20px;
  }
`;

const StyledImage = styled.img`
  border-radius: 6px;
  margin-left: 24px;
  width: 100%;

  @media(min-width: 1024px) {
    margin-left: 48px;
  }
`;

export {
  Wrapper,
  BioWrapper,
  Bio,
  StyledImage
};
