import styled from 'styled-components';

const Wrapper = styled.div`

  @media(min-width: 1024px) {
    display: flex;
    flex-direction: row;
    justify-content: center;
    margin: 0 200px;
  }
`;

const Bio = styled.p`

  @media(min-width: 1024px) {
    font-size: 16px;
  }
`;

const StyledImage = styled.img`

  @media(min-width: 1024px) {
    max-width: 400px;
  }
`;

export {
  Wrapper,
  Bio,
  StyledImage
};
