import React, { Component, Fragment } from 'react';
import { Wrapper, StyleBody } from './Style.styled.js';

class Style extends Component {
  constructor(props){
    super(props);
    this.state={};

    this.styleBodyPart1 = "\"My one main rule with regards to my technique and coaching, is that I am a resource and never going to try to change any goaltender's style. I will help the goalie build on his or her specific techniques and provide input on changes I believe will be helpful but I never take the approach of 'my way or the highway.' The goalies are the ones who innovate the position, It's the goalie coach who helps them apply their innovations. I firmly believe in continuous work on the fundamentals and age appropriate development. I try not to overcomplicate drills, focusing on body mechanics, and quality repetitions over quantity. Goaltenders develop at a faster rate by doing drills that allow them to feel the puck with gamelike shots and situations, not by drills that look fancy. I am always seeking to apply the most recent and advanced techniques to make goalies better and keep them safer.\""
  }


  render() {
    return (
      <Wrapper>
        <StyleBody>{this.styleBodyPart1}</StyleBody>
      </Wrapper>
    );
  }
}

export default Style;
