import styled from 'styled-components';
import mountains from '../../images/moutains.jpeg';

const MarqueeImage = styled.div`

  @media(min-width: 1024px) {
    background: linear-gradient(to bottom, rgba(url('../../images/moutains.jpeg'), 0.0), rgba(url('../../images/moutains.jpeg'), 1.0));
    width: 100%;
  }
`;

export {
  MarqueeImage
};
