import styled from 'styled-components';

const MenuBar = styled.div`

  @media(min-width: 1024px) {
    display: flex;
    flex-direction: row;
    justify-content: center;
  }
`;

const MenuItem = styled.p`

  @media(min-width: 1024px) {
    color: #373739;
    cursor: pointer;
    font-size: 24px;
    font-weight: bold;
    margin: 50px;
    text-direction: underline
  }

`;
export {
  MenuBar,
  MenuItem
};
