import React, { Component } from 'react';
import * as firebase from 'firebase';
import {
  Wrapper,
  FormWrapper,
  CampName,
  CampCost,
  InputWrapper,
  FlexInput,
  FullWidthInput,
  PositionSelect,
  PaymentWrapper,
  PayWithWrapper,
  VenmoLogo,
  RegisterButton,
  PaymentReminder,
  VenmoSpan,
} from './RegisterCamper.styled.js';
import venmoLogo from '../../images/venmo_logo_blue.svg';
import zeleLogo from '../../images/zele-logo.png';

class RegisterCamper extends Component {
  constructor(props){
    super(props);
    this.state={
      campCost: "Please select Player's Position to see cost",
      chargeAmount: 0,
      display: '',
      disableButton: true,
      playerFirstNameValue: '',
      playerLastNameValue: '',
      playerBirthYearValue: '',
      playerPositionValue: '',
      player2019TeamValue: '',
      playerDietValue: '',
      parentFirstNameValue: '',
      parentPhoneNumberValue: '',
      parentEmailValue: '',
    };

    this.sendForm = this.sendForm.bind(this);
    this.selectPosition = this.selectPosition.bind(this);
    this.checkIfFormValid = this.checkIfFormValid.bind(this);
  }

  sendForm() {
    let regsiterUserRef = firebase.database().ref(`/${window.sessionStorage.getItem('selectedCamp')} Campers/`).push();
    regsiterUserRef.set({
      playerFirstName: this.state.playerFirstNameValue,
      playerLastName: this.state.playerLastNameValue,
      playerBirthYear: this.state.playerBirthYearValue,
      playerPosition: this.state.playerPositionValue,
      player2019Team: this.state.player2019TeamValue,
      playerDiet: this.state.playerDietValue,
      parentFirstName: this.state.parentFirstNameValue,
      parentPhoneNumber: this.state.parentPhoneNumberValue,
      parentEmail: this.state.parentEmailValue,
    });

    this.setState({ display: "paySuccess" });
  }

  selectPosition(event) {
    this.setState({ playerPositionValue: event.target.value}, () => this.checkIfFormValid());
    if(event.target.value === 'goalie') {
      this.setState({
        campCost: `$${window.sessionStorage.getItem('selectedCampGoaliePrice')}`,
        chargeAmount: parseInt(window.sessionStorage.getItem('selectedCampGoaliePrice'))*100
      });
    } else {
      this.setState({
        campCost: `$${window.sessionStorage.getItem('selectedCampPrice')}`,
        chargeAmount: parseInt(window.sessionStorage.getItem('selectedCampPrice'))*100
      });
    }
  }

  checkIfFormValid() {
    if(
      this.state.playerFirstNameValue !== ''
      && this.state.playerLastNameValue !== ''
      && this.state.playerBirthYearValue !== ''
      && this.state.playerPositionValue !== ''
      && this.state.player2019TeamValue !== ''
      && this.state.parentFirstNameValue !== ''
      && this.state.parentPhoneNumberValue !== ''
      && this.state.parentEmailValue !== ''
    ) {
      this.setState({ disableButton: false });
    }
  }

  render() {
    if(this.state.display === 'paySuccess'){
      return(
        <Wrapper>
          <CampName>Thank you for registering!</CampName>
          <PaymentReminder>Please remember, in order to finalize your registration and officially secure your spot, you must submit a payment of {this.state.campCost} via one of the following methods.</PaymentReminder>
          <PaymentWrapper>
            <PayWithWrapper>
              <span>Pay With</span><VenmoLogo src={venmoLogo}/><span>@Karen-Rickard</span>
            </PayWithWrapper>
            <PayWithWrapper>
              <VenmoSpan>Pay With</VenmoSpan><VenmoLogo src={zeleLogo}/><span> 214-435-4978 OR kerickard18@gmail.com</span>
            </PayWithWrapper>
          </PaymentWrapper>
          <PaymentReminder>For other forms of payment (i.e. cash, check, etc.) please contact Coach K directly.</PaymentReminder>
        </Wrapper>
      )
    } else {
      return (
        <Wrapper>
          <FormWrapper>
            <CampName>Register Now for the {window.sessionStorage.getItem('selectedCamp')}</CampName>
            <CampCost>Cost: {this.state.campCost}</CampCost>
            <h2>Player Information</h2>
            <InputWrapper>
              <FlexInput
                type="text"
                autoFocus={true}
                name="PlayerFirstName"
                placeholder="Player's First Name"
                onChange={(event) => this.setState({ playerFirstNameValue: event.target.value}, () => this.checkIfFormValid())}
                value={this.state.playerFirstNameValue}
              />
              <FlexInput
                type="text"
                name="PlayerLastName"
                placeholder="Player's Last Name"
                onChange={(event) => this.setState({ playerLastNameValue: event.target.value}, () => this.checkIfFormValid())}
                value={this.state.playerLastNameValue}
              />
            </InputWrapper>
            <InputWrapper>
              <FlexInput
                type="text"
                name="PlayerBirthYear"
                placeholder="Player's Birth Year"
                onChange={(event) => this.setState({ playerBirthYearValue: event.target.value}, () => this.checkIfFormValid())}
                value={this.state.playerBirthYearValue}
              />
              <PositionSelect
                name="PlayerPosition"
                onChange={(event) => this.selectPosition(event)}
              >
                <option value="" disabled selected hidden>Player's Position</option>
                <option value="forward">Forward</option>
                <option value="defense">Defense</option>
                <option value="goalie">Goalie</option>
              </PositionSelect>
            </InputWrapper>
            <FullWidthInput
              type="text"
              name="2019Team"
              placeholder="Player's 2019 Fall/Winter Team"
              onChange={(event) => this.setState({ player2019TeamValue: event.target.value}, () => this.checkIfFormValid())}
              value={this.state.player2019TeamValue}
            />
            <FullWidthInput
              type="text"
              name="DietaryConcerns"
              placeholder="Player's Dietary Concerns If Any"
              onChange={(event) => this.setState({ playerDietValue: event.target.value}, () => this.checkIfFormValid())}
              value={this.state.playerDietValue}
            />
          </FormWrapper>
          <FormWrapper>
            <h2>Parent Information</h2>
            <InputWrapper>
              <FlexInput
                type="text"
                name="parentFirstName"
                placeholder="Parent's First Name"
                onChange={(event) => this.setState({ parentFirstNameValue: event.target.value}, () => this.checkIfFormValid())}
                value={this.state.parentFirstNameValue}
              />
              <FlexInput
                type="text"
                name="ParentLastName"
                placeholder="Parent's Last Name"
                onChange={(event) => this.setState({ parentLastNameValue: event.target.value}, () => this.checkIfFormValid())}
                value={this.state.parentLastNameValue}
              />
            </InputWrapper>
            <InputWrapper>
              <FlexInput
                type="text"
                name="ParentPhone"
                placeholder="Parent's Phone Number"
                onChange={(event) => this.setState({ parentPhoneNumberValue: event.target.value}, () => this.checkIfFormValid())}
                value={this.state.parentPhoneNumberValue}
              />
              <FlexInput
                type="text"
                name="ParentEmail"
                placeholder="Parent's Email"
                onChange={(event) => this.setState({ parentEmailValue: event.target.value}, () => this.checkIfFormValid())}
                value={this.state.parentEmailValue}
              />
            </InputWrapper>
            <PaymentWrapper>
              <PayWithWrapper>
                <span>Pay With</span><VenmoLogo src={venmoLogo}/><span>@Karen-Rickard</span>
              </PayWithWrapper>
              <PayWithWrapper>
                <VenmoSpan>Pay With</VenmoSpan><VenmoLogo src={zeleLogo}/><span> 214-435-4978 OR kerickard18@gmail.com</span>
              </PayWithWrapper>
            </PaymentWrapper>
            <RegisterButton
              disabled={this.state.disableButton}
              onClick={this.sendForm}
            >REGISTER</RegisterButton>
          </FormWrapper>
        </Wrapper>
      );
    }
  }
}

export default RegisterCamper;
