import styled from 'styled-components';

const Wrapper = styled.div`

  @media(min-width: 1024px) {
    display: flex;
    flex-direction: row;
    justify-content: center;
  }
`;

const Bio = styled.p`

  @media(min-width: 1024px) {
    font-size: 16px;
  }
`;

export {
  Wrapper,
  Bio
};
