import styled from 'styled-components';

const MarqueeGradient = styled.div`

  @media(min-width: 1024px) {
    background-image: linear-gradient(to bottom, rgba(239,239,239,255), rgba(239,239,239,0));
  }
`;

const MarqueeImage = styled.img`

  @media(min-width: 1024px) {
    width: 100%;
  }
`;

const MarqueeLogo = styled.img`
  height: 300px;
  position: absolute;

  @media(min-width: 1024px) {
    height: unset;
    top: 35px;
    width: 60%;
    z-index: 2;
  }
`;

export {
  MarqueeGradient,
  MarqueeImage,
  MarqueeLogo
};
