import styled from 'styled-components';

const Wrapper = styled.div`
  background-color: #d9dce2;

  @media(min-width: 1024px) {
    border-radius: 6px;
    display: flex;
    flex-direction: column;
    height: 512px;
    justify-content: center;
    margin: 0 150px;
    padding: 24px;
  }
`;

const StyleBody = styled.p`

  @media(min-width: 1024px) {
    font-size: 20px;
  }
`;

export {
  Wrapper,
  StyleBody
};
