import styled from 'styled-components';
import mountains from '../../images/moutains.jpeg';

const MarqueeGradient = styled.div`

  @media(min-width: 1024px) {
    background-image: linear-gradient(to bottom, rgba(239,239,239,255), rgba(239,239,239,0));
  }
`;

const MarqueeImage = styled.img`

  @media(min-width: 1024px) {
    width: 100%;
  }
`;

const MarqueeLogo = styled.img`

  @media(min-width: 1024px) {
    position: absolute;
    top: 35px;
    width: 70%;
    z-index: 2;
  }
`;

export {
  MarqueeGradient,
  MarqueeImage,
  MarqueeLogo
};
